/************************************************************************
	Plugin: Video control by scrolling - Copyleft by Dongsoo Lee - 
    download plugin: http://tmk1.co.kr/js/rolVideo.js
	sample: http://jsfiddle.net/starbros/091e2jf9/show
    
	default  usage: selector.rolVideo(proper_name);
	optional usage: selector.rolVideo(proper_name, user_button_url);
	
	★★★ .rolVideo() scripts must be used after <video> tags on <body>!!
*************************************************************************/
$.fn.rolVideo=function(t,i){i=i?i:"http://tmk1.co.kr/img/playbt.png";var o=$(this);$(window).bind("scroll resize",function(){var i=o.offset(),n=i.left,e=i.top,l=o.width(),s=o.height(),w=Math.max(0,Math.min(l,$(window).scrollLeft()+$(window).innerWidth()-n,n+l-$(window).scrollLeft())),h=Math.max(0,Math.min(s,$(window).scrollTop()+$(window).innerHeight()-e,e+s-$(window).scrollTop()));$("._bt"+t).css({position:"absolute",display:"none","z-index":"9999",left:.5*l+n-$("._bt"+t).width()/2,top:.5*s+e-$("._bt"+t).height()/2}),w*h/(l*s)>.99?$("._bt"+t).hide()&&o.get(0).play():$("._bt"+t).show()&&o.get(0).pause()}),o.after('<img src="'+i+'" class="_bt'+t+'">')};